namespace AgarIA.Web.TagHelpers;

public static class HeroIcons {
    public enum IconStyle { Outline, Solid }

    public static string Get(string name, IconStyle style = IconStyle.Outline) {
        var key = $"{name}-{style.ToString().ToLower()}";
        return Icons.TryGetValue(key, out var path) ? path : Icons.GetValueOrDefault($"{name}-outline", "");
    }

    public static string Render(string name, IconStyle style = IconStyle.Outline, string size = "6", string cssClass = null) {
        var path = Get(name, style);
        if (string.IsNullOrEmpty(path)) return "";

        var sizeClass = $"size-{size}";
        var classes = string.IsNullOrEmpty(cssClass)
            ? $"{sizeClass} inline-block shrink-0"
            : $"{sizeClass} inline-block shrink-0 {cssClass}";

        var isSolid = style == IconStyle.Solid;
        var strokeAttrs = isSolid ? "" : " stroke=\"currentColor\" stroke-width=\"1.5\"";
        var pathAttrs = isSolid ? "" : "stroke-linecap=\"round\" stroke-linejoin=\"round\" ";

        return $"<svg class=\"{classes}\" xmlns=\"http://www.w3.org/2000/svg\" viewBox=\"0 0 24 24\" fill=\"{(isSolid ? "currentColor" : "none")}\"{strokeAttrs} aria-hidden=\"true\"><path {pathAttrs}d=\"{path}\"/></svg>";
    }

